exports.vectorenGeneration = function(iterations) {
    vectoren = [[512, 512], [512, 512]];
    for (i = 0; i < iterations; i++) {
        var temp = [];
        for (j = 0; j < (Math.pow(2, i + 1) + 1); j++) {
            temp[j] = [];
        }
        for (j in vectoren) {
            for (k in vectoren[j]) {
                temp[j * 2][k * 2] = vectoren[j][k];
            }
        }
        for (x = 0; x < (Math.pow(2, i + 1) + 1); x++) {
            for (y = 0; y < (Math.pow(2, i + 1) + 1); y++) {
                if (y % 2 !== 0 || x % 2 !== 0) {
                    var dx = 0, dy = 0;
                    if (x % 2 !== 0) {
                        dx = 1;
                    }
                    if (y % 2 !== 0) {
                        dy = 1;
                    }
                    temp[x][y] = (temp[x + dx][y - dy] + temp[x - dx][y + dy]) / 2 + (Math.random()-.5) * Math.pow(2, 10 - i) / 2;
                }
            }
        }
        vectoren = temp;
    }
    return vectoren;
};